export default class Rolling{constructor(opt,callBack){this.WrapperBoxes=opt.el;this.CallBackFuntion=callBack;this.TargetNumber=this.$ConvertDigital(opt.target);this.TargetLength=String(this.TargetNumber).length;this.AnimationDelay=opt.delay!==undefined?opt.delay:300;this.AnimationDuration=opt.duration!==undefined?opt.duration:2000;this.AnimationDirection=opt.direction!==undefined?opt.direction:'random';this.MATERIALPIC='data:image/png;base64,';this.CSSSTYLE=`${this.WrapperBoxes}{font-size:0}span.rolling-number{display:inline-block;margin-top:2px;width:18px;height:20px;background:url(${this.MATERIALPIC})no-repeat;background-position:0 0;transition:${this.AnimationDuration?.1:0}s ease-out}`;this.ERRORMSG={CallBackInvalid:'请传入有效的回调函数！',UnusableElements:'请选择可被正确挂载的元素！',TargetNumberEmpty:'请输入目标数字，如果不确定，可传入与结果位数相同的字符串！',TargetNumberInvalid:'请输入合法的值，允许传入的值为 {String}[0-9] 暂不支持小数！',};this.IMGNUMBERCOUNT=9;this.defaultBaseNumber=0;this.timeoutTimer=null;this.interTimer=null;this.initPluginOptions()}initPluginOptions(){let lens=this.TargetLength;this.insertCSSTagInHead();this.insertNumberInBody(lens)}checkParamsIntegrity(CheckValue){let mountElement=this.$SelectElement(this.WrapperBoxes)[0];let isCallBackEffective=typeof(this.CallBackFuntion)==='function';if(!mountElement)throw Error(this.ERRORMSG.UnusableElements);if(!CheckValue)throw Error(this.ERRORMSG.TargetNumberEmpty);if(CheckValue=='NaN')throw Error(this.ERRORMSG.TargetNumberInvalid);if(!isCallBackEffective)throw Error(this.ERRORMSG.CallBackInvalid);}insertCSSTagInHead(){const CSSTag=document.createElement('style'),headFirstChild=document.head.firstChild;CSSTag.type='text/css';CSSTag.innerHTML=this.CSSSTYLE;document.head.insertBefore(CSSTag,headFirstChild)}insertNumberInBody(ElementLength){let componentWrap=this.$SelectElement(this.WrapperBoxes)[0],componentList=this.$CreateNumberElement(ElementLength),documentFragment=document.createDocumentFragment();componentWrap.innerHTML='';componentList.forEach(elem=>documentFragment.appendChild(elem));componentWrap.append(documentFragment)}start(newNumber=this.TargetNumber){let strinfifyNewNumber=this.$ConvertDigital(newNumber);this.checkParamsIntegrity(strinfifyNewNumber);if(strinfifyNewNumber.length!==this.TargetLength){let newNumberLength=strinfifyNewNumber.length;this.insertNumberInBody(newNumberLength)}this.startAnimation(strinfifyNewNumber)}startAnimation(newNumber){let componentList=this.$SelectElement('span.rolling-number');this.$SingleNumberRolling(componentList,newNumber);this.timeoutTimer=setTimeout(()=>{this.$ClearTimers();this.rollingToTarget(componentList,newNumber)},this.AnimationDuration)}rollingToTarget(nodeArray,numbers){let nodeArrayLength=nodeArray.length,rollingNumberItem=null,callBackTimer=null;for(let i=0;i<nodeArrayLength;i++){rollingNumberItem=nodeArray[i].style;rollingNumberItem.cssText=`background-position:0 ${numbers[i]*-30}px;`}callBackTimer=setTimeout(()=>{let PARAMS=numbers||TargetNumber;clearTimeout(callBackTimer);callBackTimer=null;this.CallBackFuntion(PARAMS)},this.AnimationDelay)}$SelectElement(elem){return document.querySelectorAll(elem)}$ConvertDigital(number){return String(Math.floor(number))}$RollingMethods(type){switch(type){case'up':return this.$RestoreNumbers('up');case'down':return this.$RestoreNumbers('down');case'random':return Math.floor(Math.random()*this.IMGNUMBERCOUNT);default:return Math.floor(Math.random()*this.IMGNUMBERCOUNT)}}$SingleNumberRolling(nodesArray,newNumber){let currentElementList=nodesArray.length,componentItem=null,randomNumber=0,newNumberCopy=newNumber,targetNumberCopy=this.TargetNumber;this.interTimer=setInterval(()=>{if(newNumberCopy===targetNumberCopy){for(let i=0;i<currentElementList;i++){componentItem=nodesArray[i].style;randomNumber=this.$RollingMethods(this.AnimationDirection);componentItem.cssText=`background-position:0 ${randomNumber*-30}px;`}}else{for(let i=0;i<currentElementList;i++){if(newNumberCopy[i]!==targetNumberCopy[i]){componentItem=nodesArray[i].style;randomNumber=this.$RollingMethods(this.AnimationDirection);componentItem.cssText=`background-position:0 ${randomNumber*-30}px;`}}}},100)}$ClearTimers(){clearInterval(this.timeoutTimer);clearInterval(this.interTimer);this.timeoutTimer=null;this.interTimer=null}$RestoreNumbers(type){if(type==='up'){return this.defaultBaseNumber>this.IMGNUMBERCOUNT?this.defaultBaseNumber=0:this.defaultBaseNumber++}else if(type==='down'){return this.defaultBaseNumber<0?this.defaultBaseNumber=this.IMGNUMBERCOUNT:this.defaultBaseNumber--}}$CreateNumberElement(ElementLength){let component=null,componentArr=[];for(let i=0;i<ElementLength;i++){component=document.createElement('span');component.className='rolling-number';componentArr.push(component)}return componentArr}}